import React from 'react';
import Card from './ProjectCard';
import { Header, Image, Modal, Container } from 'semantic-ui-react';

const RecentWorks = ({work}) => {
  if(!work){return <div>loading</div>};
  //add a card component that has the photo and title of the company/project instead of a button
  return (
    <Container style={{ height: '720px', width: '720px' }}>
      <Modal trigger={<Card work={work}></Card>}>
        <Modal.Header>{work.Organization}</Modal.Header>
        <Modal.Content image>
          <Image wrapped size='large' src={work.Photo} />
          <Modal.Description>
            <Header>{work.Blurb}</Header>
            <p>Bio: {work['Company Bio']}</p>
            <p>{work.Accomplishments}</p>
            <a href={work.Website}>website</a>
          </Modal.Description>
        </Modal.Content>
      </Modal>
    </Container>
  );
}

export default RecentWorks;
