import React from 'react';
import Card from './ProjectCard';
import { Header, Image, Modal } from 'semantic-ui-react';

const RecentWorks = ({work}) => {
  if(!work){return <div>loading</div>};
  //add a card component that has the photo and title of the company/project instead of a button
  return (
    <Modal trigger={<Card></Card>}>
      <Modal.Header>{work.Organization}</Modal.Header>
      <Modal.Content image>
        <Image wrapped size='medium' src='https://react.semantic-ui.com/images/avatar/large/rachel.png' />
        <Modal.Description>
          <Header>Title that sums up what I've done</Header>
          <p>Summary on what I did for that project in detail</p>
          <p>website link</p>
        </Modal.Description>
      </Modal.Content>
    </Modal>
  );
}

export default RecentWorks;
